    public static void NewOrder(){
        int [] order = {0,3,5,20,1,2,4,23,26,69,55,21,24,22,6,43,49,7,48,25,32,72,42,39,52,66,27,35,8,9,28,50,70,40,30,10,34,38,41,63,12,11,14,31,54,29,64,13,44,37,15,46,62,47,16,45,65,17,51,53,33,71,68,56,18,67,58,57,36,60,59,19,61};
        List<string> sceneAux = new List<string>();
        List<string> sceneNumAux = new List<string>();
        List<string> scenePathAux = new List<string>();

        for(int i = 0; i < order.Length; i++){
            string s = Scene[order[i]];
            int index = getSceneIndex("Scene"+order[i]);
            s = s.Replace("Scene"+index,"Scene"+i);
            sceneAux.Add(s);
            sceneNumAux.Add(SceneNumbers[order[i]]);
            scenePathAux.Add(ScenePath[order[i]]);
        }
        Scene = sceneAux.ToArray();
        SceneNumbers = sceneNumAux.ToArray();
        ScenePath = scenePathAux.ToArray();
    }


    public static string getSceneName(string name)
    {
        for(int i = 0; i < Scene.Length; i++)
        {
            if(Scene[i].Split(new char[1] { '$' })[0].Split(new char[1] { '|' })[5] == name)
            {
                return name.Trim();
            }
        }
        return name;
    }

    //entrega el indice donde se encuentra el string de escena 'name'
    public static int getSceneIndex(string name)
    {
        Debug.Log("Finding "+name+"...");
        for (int i = 0; i < Scene.Length; i++)
        {
            if (Scene[i].Split(new char[1] { '$' })[0].Split(new char[1] { '|' })[5].Trim() == name)
            {
                return i;
            }
        }
        return 0;
    }

